query commanderPage_CommanderQuery(
  $commander: String!
  $showStaples: Boolean!
  $showEntries: Boolean!
  $showCardDetail: Boolean!
  $cardName: String
  $sortBy: EntriesSortBy!
  $minEventSize: Int!
  $maxStanding: Int
  $timePeriod: TimePeriod!
) {
  commander(name: $commander) {
    ...commanderPage_CommanderStaples @include(if: $showStaples)
    ...commanderPage_entries @include(if: $showEntries)
    ...commanderPage_CardDetail @include(if: $showCardDetail)
    id
  }
}

fragment commanderPage_CardDetail on Commander {
  name
  cardDetail(cardName: $cardName) {
    name
    type
    cmc
    colorId
    imageUrls
    scryfallUrl
    cardPreviewImageUrl
    id
  }
  cardWinrateStats(cardName: $cardName, timePeriod: THREE_MONTHS) {
    withCard {
      totalEntries
      topCuts
      conversionRate
    }
    withoutCard {
      totalEntries
      topCuts
      conversionRate
    }
  }
  ...commanderPage_CardEntries
}

fragment commanderPage_CardEntries on Commander {
  cardEntries(cardName: $cardName, first: 48, sortBy: $sortBy) {
    edges {
      node {
        id
        ...commanderPage_EntryCard
        __typename
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
  id
}

fragment commanderPage_CommanderStaples on Commander {
  name
  staples {
    id
    name
    type
    manaCost
    scryfallUrl
    playRateLastYear
  }
}

fragment commanderPage_EntryCard on Entry {
  standing
  wins
  losses
  draws
  decklist
  player {
    name
    isKnownCheater
    offersCoaching
    id
  }
  tournament {
    name
    size
    tournamentDate
    TID
    id
  }
}

fragment commanderPage_entries on Commander {
  entries(first: 48, sortBy: $sortBy, filters: {minEventSize: $minEventSize, maxStanding: $maxStanding, timePeriod: $timePeriod}) {
    edges {
      node {
        id
        ...commanderPage_EntryCard
        __typename
      }
      cursor
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
  id
}
